choose.when:
select a.* from tb_article a, tb_user u where a.user_id = u.id and article_type_id = #{articleTypeId}
and is_pass = 1 and (a.title like CONCAT('%', #{query}, '%') or u.user_name like CONCAT('%', #{query}, '%'))
order by create_time desc

select a.* from tb_article a, tb_user u where a.user_id = u.id and article_type_id = #{articleTypeId}
and is_pass = 1 and (a.title like CONCAT('%', #{query}, '%') or u.user_name like CONCAT('%', #{query}, '%'))
and is_recommend = 1

mapper.update:
